jQuery(function($){let lastDirection;$('.-gallery').imagesLoaded(function(){$('.-gallery-spinner').fadeOut(500,function(){$('.-gallery').animate({opacity:'1'},500);});});$('.-gallery-img').on('mouseenter',function(e){const img=this;lastDirection=getDir($(this),e);$(img).addClass(lastDirection);setTimeout(function(){$(img).addClass('animated');},1);});$('.-gallery-img').on('mouseleave',function(e){$(this).removeClass(lastDirection).removeClass('animated');});});const getDir=function(elem,e){const w=elem.width();const h=elem.height();const offset=elem.offset();const x=(e.pageX-offset.left-(w/2))*(w>h?(h/w):1);const y=(e.pageY-offset.top-(h/2))*(h>w?(w/h):1);const direction=Math.round((((Math.atan2(y,x)*(180/Math.PI))+180)/90)+3)%4;switch(direction){case 0:return'top';case 1:return'right';case 2:return'bottom';case 3:return'left';}};
